/*описания задачи*/

drop procedure if exists vk_taskinfo_save
go

create procedure vk_taskinfo_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_taskinfoid varchar(38)
,atasktype varchar(38)/* тип задачи *//* тип задачи */
,aname
 varchar (255)/* название *//* название */
,acrdate
 datetime/* дата создания *//* дата создания */
,arepeat_interval
 integer/* время повтора (мин) *//* время повтора (мин) */
,alastdate
 datetime/* дата  исполнения *//* дата  исполнения */
,aresult
 varchar (255)/* результат *//* результат */
,aisdone
 integer/* завершена *//* завершена */
,acab varchar(38)/* кабинет *//* кабинет */
,acamp varchar(38)/* кампания *//* кампания */
,aads varchar(38)/* объявление *//* объявление */
,arotation varchar(38)/* ротация *//* ротация */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_taskinfo where vk_taskinfoid=g2b(avk_taskinfoid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vktsk.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_taskinfo' result;
    leave body;
  end if;
 start transaction ; 
 call vk_taskinfo_logger(acursession,avk_taskinfoid) ; 
 update  vk_taskinfo set changestamp=now()
,
  tasktype=g2b(atasktype)
,
  name=aname
,
  crdate=acrdate
,
  repeat_interval=arepeat_interval
,
  lastdate=alastdate
,
  result=aresult
,
  isdone=aisdone
,
  cab=g2b(acab)
,
  camp=g2b(acamp)
,
  ads=g2b(aads)
,
  rotation=g2b(arotation)
  where  vk_taskinfoid = g2b(avk_taskinfoid) ;


call vk_taskinfo_client_trigger(acursession,avk_taskinfoid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vktsk.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_taskinfo' result;
    leave body;
  end if;
select count(*) into aec from vk_taskinfo where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_taskinfo>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_taskinfo
 (  vk_taskinfoid 
,instanceid
,tasktype

,name

,crdate

,repeat_interval

,lastdate

,result

,isdone

,cab

,camp

,ads

,rotation

 ) values ( g2b(avk_taskinfoid) 
,g2b(ainstanceid)
,g2b(atasktype)

,aname

,acrdate

,arepeat_interval

,alastdate

,aresult

,aisdone

,g2b(acab)

,g2b(acamp)

,g2b(aads)

,g2b(arotation)

 ) ;


call vk_taskinfo_client_trigger(acursession,avk_taskinfoid);

 call vktsk_initownership( acursession,ainstanceid);
 end if;
 commit; 
select 'ok' result;
 end 
go
GO


