drop procedure if exists vk_taskmsg_brief
go

create procedure vk_taskmsg_brief  (
 acursession varchar(38),
 avk_taskmsgid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_taskmsgid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_taskmsg where vk_taskmsgid=g2b(avk_taskmsgid);
 if  aec >0 then
   set abrief=vk_taskmsg_brief_f(g2b(avk_taskmsgid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_taskmsg_delete
go

create procedure vk_taskmsg_delete /**/ (
 acursession varchar(38),
 avk_taskmsgid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_taskmsg where vk_taskmsgid=g2b(avk_taskmsgid);
if aec>0 then
 select checkoperation( acursession ,'vktsk.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_taskmsg' result;
    leave body;
  end if;
 call vk_taskmsg_logger(acursession,avk_taskmsgid) ; 
  delete from  vk_taskmsg 
  where  vk_taskmsgid = g2b(avk_taskmsgid) ;
  delete from num_values where ownerpartname='vk_taskmsg' and ownerrowid=g2b(avk_taskmsgid);
  end if;
    select 'ok' result;
 end 
go
GO

/*сообщения по задаче*/

drop procedure if exists vk_taskmsg_save
go

create procedure vk_taskmsg_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_taskmsgid varchar(38)
,amsgdate
 datetime/* дата сообщения *//* дата сообщения */
,ainfo
 varchar (255)/* сообщение *//* сообщение */
,aokmsg
 integer/* успешно *//* успешно */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_taskmsg where vk_taskmsgid=g2b(avk_taskmsgid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vktsk.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_taskmsg' result;
    leave body;
  end if;
 start transaction ; 
 call vk_taskmsg_logger(acursession,avk_taskmsgid) ; 
 update  vk_taskmsg set changestamp=now()
,
  msgdate=amsgdate
,
  info=ainfo
,
  okmsg=aokmsg
  where  vk_taskmsgid = g2b(avk_taskmsgid) ;


call vk_taskmsg_client_trigger(acursession,avk_taskmsgid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vktsk.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_taskmsg' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_taskmsg
 (  vk_taskmsgid 
,instanceid
,msgdate

,info

,okmsg

 ) values ( g2b(avk_taskmsgid) 
,g2b(ainstanceid)
,amsgdate

,ainfo

,aokmsg

 ) ;


call vk_taskmsg_client_trigger(acursession,avk_taskmsgid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists vk_taskmsg_copy
go

create procedure vk_taskmsg_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_taskmsg 
 ( vk_taskmsgid 
,instanceid
,msgdate

,info

,okmsg

 ) select 
copymap(acopyaction,b2g(vk_taskmsgid)) 
,copymap(acopyaction,b2g(instanceid))
,msgdate

,info

,okmsg

 from vk_taskmsg
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


